mapper.select:
SELECT LOG_ID, SCHEDULER, JOB_NAME, BEGIN_TIME, END_TIME, RESULT_TYPE, RESULT_INFO, CREATE_TIME FROM TB_JOB_RUN_LOG WHERE 1 = 1
AND SCHEDULER LIKE '%'||#{scheduler}||'%'
AND JOB_NAME LIKE '%'||#{jobName}||'%'
AND RESULT_TYPE = #{resultType}
AND BEGIN_TIME >= #{beginTime,jdbcType=TIMESTAMP}
AND END_TIME <= #{endTime,jdbcType=TIMESTAMP}
ORDER BY CREATE_TIME DESC

SELECT LOG_ID, SCHEDULER, JOB_NAME, BEGIN_TIME, END_TIME, RESULT_TYPE, RESULT_INFO, CREATE_TIME FROM TB_JOB_RUN_LOG WHERE 1 = 1
AND SCHEDULER LIKE '%'||#{scheduler}||'%'
AND JOB_NAME LIKE '%'||#{jobName}||'%'
AND RESULT_TYPE = #{resultType}
AND BEGIN_TIME >= #{beginTime,jdbcType=TIMESTAMP}
AND END_TIME <= #{endTime,jdbcType=TIMESTAMP}
ORDER BY CREATE_TIME DESC

mapper.delete:
DELETE FROM TB_JOB_RUN_LOG WHERE 1=1

	AND JOB_NAME = #{jobName}


	AND BEGIN_TIME >=#{beginTime}


	AND END_TIME <=#{endTime}
