mapper.select:
SELECT DISTINCT TASK.*,RUN.SUBJECT subject,RUN.PROCESS_NAME processName FROM ACT_RU_TASK TASK LEFT JOIN BPM_PRO_RUN RUN ON TASK.PROC_INST_ID_=RUN.ACT_INST_ID where TASK.ASSIGNEE_ = #{userId} and ( TASK.NAME_ like '%'||#{taskName}||'%' or TASK.ID_ in
#{taskId}
ORDER BY TASK.CREATE_TIME_ DESC

SELECT distinct task.*,run.subject subject,run.PROCESS_NAME processName
FROM ACT_RU_TASK task left join BPM_PRO_RUN run
ON task.PROC_INST_ID_=run.ACT_INST_ID where task.ID_=#{taskId}

SELECT COUNT(*) AMOUNT,t.task_id_ TASKID FROM act_ru_identitylink t where t.task_id_ in
#{taskId}
group by t.task_id_
