mapper.select:
SELECT ZJID, GDMC, GDBMC, SJCLM, SJCL_SJLX, SJCL_GS, GDZQ, GDZQ_DW, YCSJ, ZXSJ, SYBZ, BZ, ZHZXSJ, JLCJSJ, JLCJYH, JLGXSJ, JLGXYH FROM TB_GDPZ WHERE 1 = 1
AND GDMC = #{gdmc}
AND upper(GDBMC) = upper(#{gdbmc})
AND SJCLM = #{sjclm}
AND SYBZ = #{sybz}
ORDER BY JLCJSJ

SELECT COUNT(1) FROM TB_GDPZ WHERE 1=1

	AND	ZJID != #{zjid}


	AND	GDBMC = #{gdbmc}
