mapper.update:
UPDATE TB_JOB SET
	LAST_ACTIVE_TIME=SYSDATE
	WHERE JOB_NAME=#{jobName} 
		AND JOB_GROUP_NAME=#{jobGroupName}

mapper.select:
SELECT COUNT(1) FROM TB_JOB WHERE 1=1

	AND	JOB_ID != #{jobId}


	AND	TITLE = #{title}


	AND	JOB_NAME = #{jobName}


	AND	JOB_GROUP_NAME = #{jobGroupName}
