mapper.select:
SELECT ID, CATALOG_KEY, CATALOG_NAME, CATALOG_TYPE, PARENT_ID, ORDER_NO, CREATE_TIME, CREATE_USER, MODIFY_TIME, MODIFY_USER, LAYER_CODE FROM BPM_CATALOG WHERE 1 = 1
AND CATALOG_KEY = #{catalogKey}
AND CATALOG_TYPE = #{catalogType}
AND CATALOG_NAME = #{catalogName}
ORDER BY ORDER_NO

SELECT COUNT(1) FROM BPM_CATALOG WHERE 1=1

	AND	ID != #{id}


	AND	CATALOG_KEY = #{catalogKey}


	AND	CATALOG_TYPE = #{catalogType}


	AND	PARENT_ID = #{parentId}
