mapper.select:
SELECT T1.DEF_ID, T1.TYPE_ID, T1.SUBJECT, T1.DEF_KEY, T1.INST_NAME_RULE, T1.DESCRIPTION, T1.STATUS, T1.ACT_DEF_ID, T1.ACT_DEF_KEY, T1.ACT_DEPLOY_ID, T1.CREATE_TIME, T1.CREATOR, T1.MODIFY_TIME, T1.MODIFY_USER, T1.TO_FIRST_NODE, T1.PARENT_DEF_ID, T1.IS_MAIN, T1.VERSION, T1.USABLE, T1.DIRECT_START, T1.SAME_EXECUTOR_JUMP, T2.CATALOG_NAME FROM BPM_DEFINITION T1 LEFT JOIN BPM_CATALOG T2 ON T1.TYPE_ID = T2.ID WHERE 1=1
AND T1.IS_MAIN = #{isMain}
AND T1.TYPE_ID = #{typeId}
AND T1.SUBJECT LIKE '%'||#{subject}||'%'
AND T1.DEF_KEY LIKE '%'||#{defKey}||'%'
AND T1.STATUS = #{status}
AND T1.USABLE = #{usable}
AND T1.PARENT_DEF_ID = #{parentDefId}
AND T1.CREATE_TIME >= #{startTime}
AND T1.CREATE_TIME <= #{endTime}
ORDER BY MODIFY_TIME DESC

SELECT COUNT(1) FROM BPM_DEFINITION WHERE 1=1

	AND	DEF_ID != #{defId}


	AND DEF_KEY = #{defKey}


	AND TYPE_ID = #{typeId}

mapper.update:
UPDATE BPM_DEFINITION SET
	USABLE = #{usable,jdbcType=VARCHAR}
WHERE DEF_ID = #{defId}
