mapper.select:
SELECT FIELD_ID, TABLE_ID, FIELD_NAME, FIELD_TYPE, IS_REQUIRED, IS_LIST, IS_QUERY, FIELD_DESC, CHAR_LEN, INT_LEN, DECIMAL_LEN, DICT_TYPE, IS_DELETED, VALID_RULE, ORIGINAL_NAME, SN, VALUE_FROM, SCRIPT, CONTROL_TYPE, IS_HIDDEN, IS_FLOWVAR, IDENTITY, OPTIONS, CTL_PROPERTY, OPTION_FROM FROM BPM_FORM_FIELD WHERE 1 = 1
AND TABLE_ID = #{tableId}
ORDER BY SN
