mapper.select:
SELECT BTN_ID, DEF_ID, ACT_DEF_ID, NODE_ID, NODE_TYPE, BTN_NAME, TYPE, BEFORE_HANDLER, AFTER_HANDLER, SN FROM BPM_NODE_BUTTON WHERE 1 = 1
AND DEF_ID = #{defId}
AND NODE_ID = #{nodeId}
AND NODE_TYPE = #{nodeType}
ORDER BY SN

mapper.delete:
     	DELETE FROM BPM_NODE_BUTTON 
    		WHERE 1=1
   			
				AND BTN_ID = #{btnId}
			
   			
				AND DEF_ID = #{defId}
			
			
				AND NODE_ID = #{nodeId}
			
			
				AND NODE_TYPE = #{nodeType}

mapper.select:
SELECT COUNT(1) FROM BPM_NODE_BUTTON WHERE 1=1

	AND	BTN_ID != #{btnId}


	AND	BTN_NAME = #{btnName}


	AND	DEF_ID = #{defId}


	AND	NODE_ID = #{nodeId}


	AND	NODE_TYPE = #{nodeType}


	AND	TYPE = #{type}

    	SELECT MAX(SN) FROM BPM_NODE_BUTTON WHERE 1=1
    		
	    		AND	DEF_ID = #{defId}
	    	
	    	
	    		AND	NODE_ID = #{nodeId}
	    	
	    	
	    		AND	NODE_TYPE = #{nodeType}
