mapper.select:
SELECT PARTICIPANT_ID, CONFIG_ID, DEF_ID, ACT_DEF_ID, NODE_ID, PARTICIPANT_TYPE, PARTICIPAN, PARTICIPAN_DESC, COMPUTE_TYPE, SN, EXTRACT_USER FROM BPM_NODE_PARTICIPANT WHERE 1 = 1
AND PARTICIPANT_TYPE = #{participantType}
AND DEF_ID = #{defId}
AND ACT_DEF_ID = #{actDefId}
AND NODE_ID = #{nodeId}
AND CONFIG_ID = #{configId}
ORDER BY SN

mapper.update:
UPDATE BPM_NODE_PARTICIPANT SET
	SN = #{sn,jdbcType=INTEGER}
WHERE PARTICIPANT_ID=#{participantId}
