mapper.select:
SELECT RUN_ID, DEF_ID, PROCESS_NAME, SUBJECT, CREATOR_ID, CREATOR, CREATE_TIME, BUS_DESCP, STATUS, ACT_INST_ID, ACT_DEF_ID, BUSINESS_KEY, BUSINESS_URL, END_TIME, DURATION, PK_NAME, TABLE_NAME, PARENT_ID, START_ORG_ID, START_ORG_NAME, FORM_DEF_ID, TYPE_ID, FLOW_KEY, FORM_KEY_URL, LAST_SUBMIT_DURATION, IS_FORMAL FROM BPM_PRO_RUN WHERE STATUS != 10
AND DEF_ID = #{defId}
AND PROCESS_NAME LIKE '%'||#{processName}||'%'
AND SUBJECT LIKE '%'||#{subject}||'%'
AND STATUS = #{status}
AND CREATE_TIME >= #{startTime}
AND CREATE_TIME < = #{endTime}
ORDER BY CREATE_TIME

SELECT RUN_ID, DEF_ID, PROCESS_NAME, SUBJECT, CREATOR_ID, CREATOR, CREATE_TIME, BUS_DESCP, STATUS, ACT_INST_ID, ACT_DEF_ID, BUSINESS_KEY, BUSINESS_URL, END_TIME, DURATION, PK_NAME, TABLE_NAME, PARENT_ID, START_ORG_ID, START_ORG_NAME, FORM_DEF_ID, TYPE_ID, FLOW_KEY, FORM_KEY_URL, LAST_SUBMIT_DURATION, IS_FORMAL FROM BPM_PRO_RUN WHERE STATUS != 10
AND DEF_ID = #{defId}
AND PROCESS_NAME LIKE '%'||#{processName}||'%'
AND SUBJECT LIKE '%'||#{subject}||'%'
AND STATUS = #{status}
AND CREATE_TIME >= #{startTime}
AND CREATE_TIME < = #{endTime}
ORDER BY CREATE_TIME DESC

    	SELECT DISTINCT RUN.*
		  FROM BPM_PRO_RUN RUN
		  LEFT JOIN BPM_DEFINITION DEF ON RUN.DEF_ID = DEF.DEF_ID
		 WHERE RUN.CREATOR_ID = #{creatorId}
		   AND RUN.STATUS IN (1, 5, 6)
		   AND DEF.STATUS != 3
			 AND UPPER(RUN.SUBJECT) LIKE '%'||UPPER(#{subject})||'%'
			 AND RUN.CREATE_TIME  >=#{startTime,jdbcType=DATE} 
			 AND  RUN.CREATE_TIME <= #{endTime,jdbcType=DATE}   
			 AND RUN.STATUS  = #{status} 
			 AND UPPER(RUN.PROCESS_NAME)  LIKE '%'||UPPER(#{processName})||'%'
			ORDER BY RUN.CREATE_TIME DESC

    	SELECT DISTINCT RUN.*
		  FROM BPM_PRO_RUN RUN
		  LEFT JOIN BPM_DEFINITION DEF ON RUN.DEF_ID = DEF.DEF_ID
		 WHERE RUN.CREATOR_ID = #{creatorId}
		   AND (RUN.STATUS = 2 OR  RUN.STATUS = 3 ) 
		   AND DEF.STATUS != 3
			 AND UPPER(RUN.SUBJECT) LIKE '%'||UPPER(#{subject})||'%'
			 AND RUN.CREATE_TIME  >=#{startTime,jdbcType=DATE} 
			 AND  RUN.CREATE_TIME <= #{endTime,jdbcType=DATE}   
			 AND RUN.STATUS  = #{status} 
			 AND UPPER(RUN.PROCESS_NAME)  LIKE '%'||UPPER(#{processName})||'%'
			ORDER BY RUN.CREATE_TIME DESC
