mapper.select:
	SELECT DM.ZJID,DM.DMMC,DM.DMJC,DM.DMX_BH,DM.DMX_MC,DM.SFDMX,DM.SYBZ,DM.PXH,DM.BZ,TO_CHAR(TO_DATE(DM.JLXGSJ,'YYYYMMDDHH24MISS'),'YYYY-MM-DD HH24:MI:SS')JLXGSJ
    FROM TB_DMXX DM
    WHERE 1=1
    and dm.DMJC like '%'|| #{dmjc}||'%'
    and DM.SFDMX = #{sfdmx}
    and DM.SYBZ = #{sybz}
    and DM.JLYXZT = #{jlyxzt}
    order by to_number(dm.pxh)

select dm.zjid,dm.dmmc,dm.dmjc,dm.dmx_bh,dm.dmx_mc,dm.sfdmx,dm.sybz,dm.pxh,dm.bz,to_char(to_date(dm.jlxgsj,'yyyyMMddHH24miss'),'yyyy-MM-dd HH24:mi:ss')jlxgsj
from TB_DMXX dm
where 1 = 1
and dm.ZJID = #{zjid}
and dm.DMMC = #{dmmc}
and upper(dm.DMJC)  = upper(#{dmjc})		
and dm.SFDMX = #{sfdmx}
and upper(dm.DMX_BH) LIKE '%'||upper(#{dmx_bh}) ||'%'
and dm.DMX_MC LIKE '%'||#{dmx_mc} ||'%'		
and dm.PXH = #{pxh}
and dm.SYBZ = #{sybz}		
and dm.CJYH = #{cjyh}
and dm.GXYH = #{gxyh}		
and dm.JLYXZT = #{jlyxzt}		  
order by to_number(dm.pxh)

select dm.* 
from TB_DMXX dm
where 1 = 1
and dm.ZJID = #{zjid}
and dm.DMMC = #{dmmc}
and dm.DMJC  = #{dmjc}		
and dm.SFDMX = #{sfdmx}
and dm.DMX_BH = #{dmx_bh}
and dm.DMX_MC = #{dmx_mc}		
and dm.PXH = #{pxh}
and dm.SYBZ = #{sybz}		
and dm.CJYH = #{cjyh}
and dm.GXYH = #{gxyh}		
and dm.BZ = #{bz}
and dm.JLYXZT = #{jlyxzt}

mapper.insert:
 
	SELECT SEQ_TB_DMXX.NEXTVAL FROM DUAL 

insert into TB_DMXX(ZJID,DMMC,DMJC,SFDMX,SYBZ,PXH,JLYXZT
		 ,DMX_BH 
		 ,DMX_MC 
		 ,BZ 
		 ,CJYH 
		 ,GXYH 
		 ,JLXZSJ 
		 ,JLXGSJ 
		 ,JLSCSJ 		
	)
values (#{zjid},#{dmmc},#{dmjc},#{sfdmx},#{sybz},#{pxh},#{jlyxzt}
		 ,#{dmx_bh} 
		 , #{dmx_mc} 
		 , #{bz} 
		 , #{cjyh} 
		 , #{gxyh} 
		 ,#{jlxzsj} 
		 ,#{jlxgsj} 
		 ,#{jlscsj} 	
	)

mapper.update:
update TB_DMXX
set ZJID = ZJID
,DMMC = #{dmmc}
,DMJC  = #{dmjc}		
,SFDMX = #{sfdmx}
,DMX_BH = #{dmx_bh}
,DMX_MC = #{dmx_mc}		
,PXH = #{pxh}
,SYBZ = #{sybz}
 ,BZ = #{bz}
 ,BZ = ''
,JLYXZT = #{jlyxzt}	
,CJYH = #{cjyh}
 ,GXYH = #{gxyh}		
,JLXZSJ = #{jlxzsj} 
 ,JLXGSJ = #{jlxgsj} 
 ,JLSCSJ = #{jlscsj} 
where ZJID = #{zjid}
