SELECT LOG_ID, ORG_ID, ORG_NO, ORG_NAME, ID_CARD, POLICE_ID, USER_ID, USER_NAME, LOGIN_ID, OPERATE_TIME, TERMINAL_ID, OPERATE_CONTENT, OPERATE_RESULT, OPERATE_DESC, MODULE_NAME, SYS_NAME, LOG_TYPE FROM TB_SYS_LOG WHERE 1 = 1
AND POLICE_ID = #{policeId}
AND TERMINAL_ID = #{terminalId}
AND LOG_TYPE = #{logType}
AND OPERATE_TIME >= #{startTime}
AND OPERATE_TIME <= #{endTime}
ORDER BY OPERATE_TIME DESC

