mapper.select:
SELECT C.*,C1.CDMC AS SJCDCDMC
FROM TB_CDXX C
LEFT JOIN TB_CDXX C1 ON C.SJCD = C1.ZJID WHERE 1 = 1
and C.ZJID = #{zjId}
and C.CDMC = #{cdmc}
and C.SJCD = #{sjcd}
and C.CDJB = #{cdjb}
and C.SFZHYICD = #{sfzhyicd}
and C.SYBZ = #{sybz}
and C.JLYXZT = #{jlyxzt}
order by to_number(C.CDXH)

SELECT ZJID,CDMC,CDJB,CDXH,SJCD,CDDZ,SFZHYICD,SYBZ,BZ,CJYH,GXYH,JLXZSJ,JLXGSJ,JLSCSJ,JLYXZT 
FROM TB_CDXX WHERE 1=1

	AND ZJID=#{zjId}


	AND SJCD=#{sjcd}


	AND SYBZ=#{sybz}


	AND JLYXZT=#{jlyxzt}

SELECT * FROM TB_CDXX
	CONNECT BY PRIOR SJCD=ZJID
	START WITH ZJID=#{menuId}

SELECT DISTINCT(T1.ZJID) AS CDID,T5.ZJID AS YHID,T1.CDMC,T5.YHMC,T5.JYBH, T5.SFZH,T6.DWMC FROM TB_CDXX T1 JOIN TB_GNXX T2 ON T1.ZJID = T2.CDXX_ZJID JOIN TB_JSXX_R_GNXX T3 ON T2.ZJID = T3.GNXX_ZJID JOIN TB_YHXX_R_JSXX T4 ON T3.JSXX_ZJID = T4.JSXX_ZJID JOIN TB_YHXX T5 ON T4.YHXX_ZJID = T5.ZJID JOIN TB_DWXX T6 ON T5.SSDW_ZJID = T6.ZJID WHERE T1.ZJID IN
#{menuId}
AND T5.SYBZ =1 AND T5.JLYXZT=1 UNION SELECT DISTINCT(T1.ZJID) AS CDID,T5.ZJID AS YHID,T1.CDMC,T5.YHMC,T5.JYBH, T5.SFZH,T6.DWMC FROM TB_CDXX T1 JOIN TB_GNXX T2 ON T1.ZJID = T2.CDXX_ZJID JOIN TB_JSXX_R_GNXX T3 ON T2.ZJID = T3.GNXX_ZJID JOIN TB_DWXX_R_JSXX T4 ON T3.JSXX_ZJID = T4.JSXX_ZJID JOIN TB_YHXX T5 ON T4.DWXX_ZJID = T5.SSDW_ZJID JOIN TB_DWXX T6 ON T5.SSDW_ZJID = T6.ZJID WHERE T1.ZJID IN
#{menuId}
AND T5.SYBZ =1 AND T5.JLYXZT=1

mapper.update:
UPDATE TB_CDXX
   SET JLYXZT = '0', JLSCSJ = #{jlscsj}
 WHERE ZJID IN (SELECT ZJID
                  FROM TB_CDXX
                 START WITH ZJID = #{zjId}
                CONNECT BY PRIOR ZJID = SJCD)
