mapper.select:
SELECT ZJID,DWBH,DWMC, DWJC,DWDH,DWYX, DWFZR,SJDW_ZJID,DWLX, DWJB,BZ,CJYH,GXYH, JLYXZT,JLXZSJ,JLXGSJ FROM TB_DWXX A WHERE 1 = 1
and A.ZJID = #{zjid}
AND A.DWBH LIKE '%'||#{dwbh}||'%'
AND A.DWMC LIKE '%'||#{dwmc}||'%'
AND A.DWJC LIKE '%'||#{dwjc}||'%'
AND A.SJDW_ZJID = #{sjdw_zjid}
AND A.DWFZR LIKE '%'||#{dwfzr}||'%'
AND A.JLYXZT = #{jlyxzt}
ORDER BY A.DWBH

SELECT A.ZJID,A.DWBH,A.DWMC,A.DWJC,A.DWDH,A.DWYX,A.DWFZR,A.SJDW_ZJID,A.DWLX,A.DWJB,A.BZ,A.CJYH,A.GXYH,A.JLYXZT, A.JLXZSJ,A.JLXGSJ,B.DWMC SJDWMC FROM (SELECT * FROM TB_DWXX DW WHERE 1=1
START WITH DW.ZJID = #{zjid} CONNECT BY PRIOR DW.ZJID = DW.SJDW_ZJID
) A LEFT JOIN TB_DWXX B ON A.SJDW_ZJID = B.ZJID WHERE 1 = 1
AND A.DWBH LIKE '%'||#{dwbh}||'%'
AND A.DWMC LIKE '%'||#{dwmc}||'%'
AND A.DWJC LIKE '%'||#{dwjc}||'%'
AND A.SJDW_ZJID = #{sjdw_zjid}
AND A.DWFZR like '%'||#{dwfzr}||'%'
AND A.JLYXZT = #{jlyxzt}
ORDER BY A.DWBH

mapper.update:
UPDATE TB_DWXX SET DWBH = #{dwbh,jdbcType=VARCHAR}, DWMC = #{dwmc,jdbcType=VARCHAR},
				   DWJC = #{dwjc,jdbcType=VARCHAR}, DWDH = #{dwdh,jdbcType=VARCHAR},
				   DWYX = #{dwyx,jdbcType=VARCHAR}, DWFZR = #{dwfzr,jdbcType=VARCHAR},
				   SJDW_ZJID = #{sjdw_zjid,jdbcType=VARCHAR}, DWLX = #{dwlx,jdbcType=VARCHAR},
				   DWJB = #{dwjb,jdbcType=VARCHAR}, BZ = #{bz,jdbcType=VARCHAR}
WHERE ZJID = #{zjid}

mapper.select:
SELECT * FROM TB_DWXX
	CONNECT BY PRIOR SJDW_ZJID=ZJID
	START WITH ZJID=#{orgId}

SELECT * FROM TB_DWXX
	CONNECT BY PRIOR SJDW_ZJID=ZJID
	START WITH ZJID=#{orgId}

select count(0) from TB_DWXX
	where DWBH = #{dwbh}

	and ZJID != #{zjid}

mapper.update:
UPDATE TB_DWXX SET 
	JLYXZT = #{jlyxzt},
	JLSCSJ = #{jlscsj}
WHERE ZJID = #{zjid}

