mapper.select:
SELECT ZJID, JSMC, JSLX, JSJB, SYBZ, BZ,
CJYH,GXYH,JLXZSJ,JLXGSJ,JLSCSJ,
JLYXZT,JSDM FROM TB_JSXX
WHERE 1=1

	and ZJID = #{zjid}


	and JSMC like '%'||#{jsmc}||'%'


	and JSDM like '%'||#{jsdm}||'%'


	and JSLX = #{jslx}


	and JSJB >= #{jsjb}


	and SYBZ = #{sybz}


	and BZ like '%'||#{bz}||'%'


	and JLYXZT=#{jlyxzt}

ORDER BY JLXGSJ DESC

SELECT ZJID, JSMC, JSLX, JSJB, SYBZ, BZ,
CJYH,GXYH,JLXZSJ,JLXGSJ,JLSCSJ,
JLYXZT,JSDM FROM TB_JSXX
WHERE 1=1

	and ZJID = #{zjid}


	and JSMC like '%'||#{jsmc}||'%'


	and JSDM like '%'||#{jsdm}||'%'


	and JSLX = #{jslx}


	and JSJB >= #{jsjb}


	and SYBZ = #{sybz}


	and BZ like '%'||#{bz}||'%'


	and JLYXZT=#{jlyxzt}

ORDER BY JLXGSJ DESC

mapper.update:
UPDATE TB_JSXX SET ZJID=ZJID
	,JSMC=#{jsmc,jdbcType=VARCHAR}
	,JSLX=#{jslx,jdbcType=VARCHAR}
	,JSDM=#{jsdm,jdbcType=VARCHAR}
	,JSJB=#{jsjb,jdbcType=VARCHAR}
	,BZ=#{bz,jdbcType=VARCHAR}
	,GXYH=#{gxyh,jdbcType=VARCHAR}
	,JLXGSJ=#{jlxgsj,jdbcType=VARCHAR}
WHERE ZJID=#{zjid}

UPDATE TB_JSXX SET 
	SYBZ=#{sybz},
	GXYH=#{gxyh,jdbcType=VARCHAR},
	JLXGSJ=#{jlxgsj,jdbcType=VARCHAR}
	WHERE ZJID=#{zjid}

UPDATE TB_JSXX SET 
	JLYXZT=#{jlyxzt,jdbcType=VARCHAR},
	JLXGSJ=#{jlxgsj,jdbcType=VARCHAR}
	WHERE ZJID=#{zjid}

mapper.select:
SELECT COUNT(0) FROM TB_JSXX
WHERE JLYXZT = 1

	and ZJID != #{zjid}


	and JSDM = #{jsdm}
