mapper.select:
SELECT ZJID,YHMC,YHDLZH,SFZH,YHDLMM,SFZH,JYBH,SSDW_ZJID,XB,LXDH,YDDH,
	DZYX,YHLX,YHJB,SYBZ,BZ,CJYH,GXYH,
	JLXZSJ,JLXGSJ,JLSCSJ,JLYXZT from TB_YHXX 
	where 1=1 

	and ZJID=#{zjid}


	and YHMC=#{yhmc}


	and SSDW_ZJID=#{ssdw_zjid}



	and SYBZ=#{sybz}


	and JLYXZT=#{jlyxzt}

SELECT B.ZJID, B.YHMC, B.YHDLZH, B.YHDLMM, B.SFZH, B.JYBH, B.SSDW_ZJID, B.XB, B.LXDH, B.YDDH, B.DZYX, B.YHLX, B.YHJB, B.SYBZ, B.BZ, B.CJYH, B.GXYH, B.JLXZSJ, B.JLXGSJ, B.JLSCSJ, B.JLYXZT, A.DWMC FROM TB_YHXX B, TB_DWXX A WHERE A.ZJID = B.SSDW_ZJID
and b.YHMC like '%'||#{yhmc}||'%'
and b.YHDLZH like '%'||#{yhdlzh}||'%'
and b.JYBH like '%'||#{jybh}||'%'
and b.LXDH like '%'||#{lxdh}||'%'
and b.YDDH like '%'||#{yddh}||'%'
and b.DZYX like '%'||#{dzyx}||'%'
and b.YHLX = #{yhlx}
and b.YHJB >= #{yhjb}
and b.SYBZ = #{sybz}
and b.JLYXZT = #{jlyxzt}
AND A.ZJID IN (SELECT DW.ZJID FROM TB_DWXX DW WHERE 1 = 1 START WITH DW.ZJID = #{ssdw_zjid} CONNECT BY PRIOR DW.ZJID = DW.SJDW_ZJID)
ORDER BY B.ZJID

SELECT B.ZJID, B.YHMC, B.YHDLZH, B.YHDLMM, B.SFZH, B.JYBH, B.SSDW_ZJID, B.XB, B.LXDH, B.YDDH, B.DZYX, B.YHLX, B.YHJB, B.SYBZ, B.BZ, B.CJYH, B.GXYH, B.JLXZSJ, B.JLXGSJ, B.JLSCSJ, B.JLYXZT, A.DWMC FROM TB_YHXX B, TB_DWXX A WHERE A.ZJID = B.SSDW_ZJID
and B.SSDW_ZJID = #{ssdwZjid}
and b.SYBZ = #{sybz}
and b.JLYXZT = #{jlyxzt}
ORDER BY B.ZJID

mapper.update:
UPDATE TB_YHXX SET 
	YHMC=#{yhmc,jdbcType=VARCHAR}
	,YHDLZH=#{yhdlzh,jdbcType=VARCHAR}
	,SFZH=#{sfzh,jdbcType=VARCHAR}
	,JYBH=#{jybh,jdbcType=VARCHAR}
	,SSDW_ZJID=#{ssdw_zjid,jdbcType=VARCHAR}
	,XB=#{xb,jdbcType=VARCHAR}
	,LXDH=#{lxdh,jdbcType=VARCHAR}
	,YDDH=#{yddh,jdbcType=VARCHAR}
	,DZYX=#{dzyx,jdbcType=VARCHAR}
	,YHLX=#{yhlx,jdbcType=VARCHAR}
	,YHJB=#{yhjb,jdbcType=VARCHAR}
	,SYBZ=#{sybz,jdbcType=VARCHAR}
	,BZ=#{bz,jdbcType=VARCHAR}
	,GXYH=#{gxyh,jdbcType=VARCHAR}
	,JLXGSJ=#{jlxgsj,jdbcType=VARCHAR}
WHERE ZJID=#{zjid}

UPDATE TB_YHXX SET 
	SYBZ=#{sybz,jdbcType=VARCHAR},
	JLXGSJ=#{jlxgsj,jdbcType=VARCHAR},
	GXYH=#{gxyh,jdbcType=VARCHAR}
	WHERE ZJID=#{zjid}

UPDATE TB_YHXX SET 
	JLYXZT=#{jlyxzt,jdbcType=VARCHAR},
	JLSCSJ=#{jlscsj,jdbcType=VARCHAR}
	WHERE ZJID=#{zjid}

UPDATE TB_YHXX SET 
	SSDW_ZJID=#{ssdw_zjid,jdbcType=VARCHAR},
	JLXGSJ=#{jlxgsj,jdbcType=VARCHAR},
	GXYH=#{gxyh,jdbcType=VARCHAR}
	WHERE ZJID=#{zjid}

UPDATE TB_YHXX SET 
	YHDLMM=#{yhdlmm,jdbcType=VARCHAR},
	JLXGSJ=#{jlxgsj,jdbcType=VARCHAR},
	GXYH=#{gxyh,jdbcType=VARCHAR}
	WHERE ZJID=#{zjid}

mapper.select:
select count(0) from TB_YHXX
	where 1=1

	AND ZJID!=#{zjid}

 
	AND JYBH=#{jybh}

 
	AND YHDLZH=#{yhdlzh}

 
	AND SFZH=#{sfzh}

SELECT T1.*,T3.DWMC FROM TB_YHXX T1, TB_YHXX_R_JSXX T2, TB_DWXX T3 WHERE T1.ZJID = T2.YHXX_ZJID AND T1.SSDW_ZJID = T3.ZJID AND T1.SYBZ = 1 AND T1.JLYXZT = '1' AND T2.JSXX_ZJID = #{roleId}
AND T3.ZJID IN (SELECT DW.ZJID FROM TB_DWXX DW WHERE 1 = 1 START WITH DW.ZJID = #{rootOrgId} CONNECT BY PRIOR DW.ZJID = DW.SJDW_ZJID)
ORDER BY T1.ZJID

SELECT T1.* FROM TB_YHXX T1,TB_YHXX_R_JSXX T2 WHERE T1.ZJID = T2.YHXX_ZJID(+) AND T1.SYBZ = '1' AND T1.JLYXZT = '1' 
   AND T2.JSXX_ZJID IN 
     
        #{zjid}
